function Auth(){self=this,self.mask_wrapper=$(".mask_wrapper"),self.scrollGroup=$(".scroll_group")}Auth.prototype.showEvent=function(){self=this,self.mask_wrapper.show()},Auth.prototype.hideEvent=function(){self=this,self.mask_wrapper.hide()},Auth.prototype.listenEvent=function(){self=this;var e=$(".signin"),n=$(".signup"),t=$("#close_btn"),s=$(".switch");e.click(function(){self.showEvent(),console.log("login in"),self.scrollGroup.css({left:0})}),n.click(function(){self.showEvent(),console.log("login out"),self.scrollGroup.css({left:-400})}),t.click(function(){self.hideEvent()}),s.click(function(){var e=self.scrollGroup.css("left");(e=parseInt(e))<0?self.scrollGroup.animate({left:"0px"}):self.scrollGroup.animate({left:"-400px"})})},Auth.prototype.listenRequestEvent=function(){self=this;var e=$(".signin_group"),s=e.find("input[name='telephone']"),o=e.find("input[name='password']"),l=e.find("input[name='remember']");e.find(".submit_btn").click(function(){var e=s.val(),n=o.val(),t=l.prop("checked");$.post("/account/login/",{telephone:e,password:n,remember:t?1:0},function(e){console.log(e),200==e.code&&self.hideEvent()})})},Auth.prototype.run=function(){self=this,self.listenEvent(),self.listenRequestEvent()},$(function(){(new Auth).run()});