function Auth(){self=this,self.mask_wrapper=$(".mask_wrapper"),self.scrollGroup=$(".scroll_group")}Auth.prototype.showEvent=function(){self=this,self.mask_wrapper.show()},Auth.prototype.hideEvent=function(){self=this,self.mask_wrapper.hide()},Auth.prototype.listenEvent=function(){self=this;var e=$(".signin"),t=$(".signup"),n=$("#close_btn"),s=$(".switch");e.click(function(){self.showEvent(),self.scrollGroup.css("left:0")}),t.click(function(){self.showEvent(),self.scrollGroup.css("left:-400")}),n.click(function(){self.hideEvent()}),s.click(function(){var e=self.scrollGroup.css("left");(e=parseInt(e))<0?self.scrollGroup.animate({left:"0px"}):self.scrollGroup.animate({left:"-400px"})})},Auth.prototype.listenRequestEvent=function(){self=this;var e=$(".signin_group"),s=e.find("input[name='telephone']"),l=e.find("input[name='password']"),o=e.find("input[name='remember']");e.find(".submit_btn").click(function(){var e=s.val(),t=l.val(),n=o.prop("checked");$.post("/account/login/",{telephone:e,password:t,remember:n?1:0},function(e){console.log("========"),console.log(e),console.log("========")})})},Auth.prototype.run=function(){self=this,self.listenEvent(),self.listenRequestEvent()},$(function(){(new Auth).run()});