function NewsList(){}NewsList.prototype.listenSubmitEvent=function(){var n=$(".submit_btn"),s=$("textarea[name='comment']");n.click(function(){var t=s.val(),e=n.attr("data-news-id");$.post({url:"/news/comment/",data:{news_id:e,content:t},success:function(t){if(200===t.code){var e=t.data,n=template("comment-item",{comment:e});$(".comment_list").prepend(n),window.messageBox.showSuccess("评论发表成功~"),s.val("")}else window.messageBox.showError(t.message)}})})},NewsList.prototype.run=function(){self=this,self.listenSubmitEvent()},$(function(){(new NewsList).run()});